import React, { useEffect } from 'react';
import { ArrowDown } from './Icons';

const RAINBOW_COLORS_HEX = ['#EF4444', '#F97316', '#EAB308', '#22C55E', '#3B82F6', '#6366F1', '#8B5CF6'];
const RAINBOW_COLORS_TW_HOVER = [
    'hover:bg-red-500', 'hover:bg-orange-500', 'hover:bg-yellow-500',
    'hover:bg-green-500', 'hover:bg-blue-500', 'hover:bg-indigo-500', 'hover:bg-violet-500'
];

function WelcomePage({ onScrollToMain, onDevButtonClick }) {
    useEffect(() => {
        // 마우스 휠 스크롤 감지 시 메인 페이지로 전환
        const handleScroll = (e) => {
            if (e.deltaY > 0) { // 아래로 스크롤할 때만
                onScrollToMain();
            }
        };

        // 터치 스와이프 감지
        let touchStartY = 0;
        const handleTouchStart = (e) => {
            touchStartY = e.touches[0].clientY;
        };
        const handleTouchMove = (e) => {
            const touchEndY = e.touches[0].clientY;
            const deltaY = touchStartY - touchEndY;
            if (deltaY > 50) { // 50px 이상 위로 스와이프
                onScrollToMain();
            }
        };

        window.addEventListener('wheel', handleScroll);
        window.addEventListener('touchstart', handleTouchStart);
        window.addEventListener('touchmove', handleTouchMove);

        return () => {
            window.removeEventListener('wheel', handleScroll);
            window.removeEventListener('touchstart', handleTouchStart);
            window.removeEventListener('touchmove', handleTouchMove);
        };
    }, [onScrollToMain]);

    return (
        <div className="min-h-screen w-full flex flex-col justify-center items-center bg-gradient-to-br from-purple-50 via-white to-indigo-50 relative overflow-hidden">
            {/* 
                이미지를 넣으려면:
                1. public/images/ 폴더에 이미지 저장 (예: logo.png)
                2. 아래처럼 사용:
                <img src="/images/logo.png" alt="Logo" className="w-32 h-32 mb-8" />
            */}

            {/* 메인 타이틀 */}
            <div className="text-center mb-16 px-4">
                <h1 className="text-5xl md:text-7xl font-light mb-6 text-transparent bg-clip-text bg-gradient-to-r from-purple-600 to-indigo-600">
                    Crosstalk
                </h1>
                <p className="text-lg md:text-2xl text-gray-600 font-light">
                    당신의 마음을 다시 연결합니다
                </p>
            </div>

            {/* 스크롤 안내 */}
            <div className="flex flex-col items-center text-gray-500 mb-12">
                <p className="text-sm mb-2">아래로 스크롤하세요</p>
                <ArrowDown size={32} className="scroll-arrow" />
            </div>

            {/* 개발자용 버튼 (우측 하단) */}
            <div className="absolute bottom-8 right-8 flex flex-col space-y-2">
                {RAINBOW_COLORS_HEX.map((color, index) => (
                    <button
                        key={index}
                        onClick={() => onDevButtonClick(index)}
                        className={`w-12 h-12 rounded-full shadow-lg transition-all transform hover:scale-110 ${RAINBOW_COLORS_TW_HOVER[index]}`}
                        style={{ backgroundColor: color }}
                        title={`시나리오 ${index + 1} 설정`}
                    ></button>
                ))}
            </div>
        </div>
    );
}

export default WelcomePage;
