import React, { useEffect } from 'react';

const LOADING_TEXT = "마음은 아직 끝나지 않았습니다. 이별은 단지 프로그램의 일시정지일 뿐입니다. 우리 AI는 당신의 감정을 복원하고, 그 사람의 마음과 다시 '주파수'를 맞춥니다. 눈을 감고 생각해보세요. 그때의 대화, 그때의 온도. 이제 당신이 할 일은 단 하나입니다. 다시 연결되길 '허락'하는 것.";

function LoadingPage({ onLoadingComplete }) {
    useEffect(() => {
        // 5초 후 채팅방으로 이동
        const timer = setTimeout(() => {
            onLoadingComplete();
        }, 5000);
        return () => clearTimeout(timer);
    }, [onLoadingComplete]);

    return (
        <div className="min-h-screen w-full flex flex-col justify-center items-center bg-white relative overflow-hidden">
            {/* 3겹 스피너 */}
            <div className="spinner-container mb-16">
                <div className="spinner-ring spinner-ring-1"></div>
                <div className="spinner-ring spinner-ring-2"></div>
                <div className="spinner-ring spinner-ring-3"></div>
            </div>

            {/* 흐르는 텍스트 */}
            <div className="marquee-container">
                <div className="marquee-text">
                    {LOADING_TEXT}
                </div>
            </div>
        </div>
    );
}

export default LoadingPage;
