import React, { useState, useEffect, useRef } from 'react';
import { Send, LogOut } from './Icons';

function MobileChatPage({ messages, setMessages, activeScenarioIndex, scenarios, onExit }) {
    const [input, setInput] = useState('');
    const [isAiTyping, setIsAiTyping] = useState(false);
    const [scenarioStep, setScenarioStep] = useState(0);
    const chatEndRef = useRef(null);

    // 채팅창 자동 스크롤
    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    // 초기 AI 메시지 설정
    useEffect(() => {
        if (messages.length === 0) {
            setMessages([
                { sender: 'ai', text: '안녕하세요, Crosstalk AI 상담사입니다. 편안하게 이야기 나눠주세요. 어떤 이야기를 나누고 싶으신가요?' }
            ]);
        }
    }, [messages.length, setMessages]);

    // AI 응답 로직
    const getAiResponse = () => {
        setIsAiTyping(false);
        let aiText;

        // 활성화된 시나리오가 있는지 확인
        if (activeScenarioIndex !== null) {
            const scenario = scenarios[activeScenarioIndex];
            if (scenario.responses && scenario.responses.length > 0) {
                aiText = scenario.responses[scenarioStep];
                // 다음 응답 순서로 이동 (순환)
                setScenarioStep(prev => (prev + 1) % scenario.responses.length);
            }
        }

        // 시나리오가 없거나 응답이 비어있으면 기본 응답
        if (!aiText) {
            aiText = '그렇군요. 좀 더 자세히 말씀해 주시겠어요? 당신의 이야기에 귀 기울이고 있어요.';
        }

        const newAiMessage = { sender: 'ai', text: aiText };
        setMessages(prev => [...prev, newAiMessage]);
    };

    // 메시지 전송 핸들러
    const handleSend = () => {
        if (input.trim() === '') return;

        const newUserMessage = { sender: 'user', text: input };
        setMessages(prev => [...prev, newUserMessage]);
        setInput('');
        setIsAiTyping(true);

        // Mock AI 응답 (1.5초 딜레이)
        setTimeout(getAiResponse, 1500);
    };

    // Enter 키 전송 (Shift+Enter는 줄바꿈)
    const handleKeyDown = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSend();
        }
    };

    return (
        <div className="h-screen w-full flex flex-col bg-white font-light max-w-[430px] mx-auto">
            {/* Galaxy S25 비율: 19.5:9 (430x932px 기준) */}
            
            {/* 채팅 헤더 - 모바일 최적화 */}
            <header className="flex-shrink-0 flex justify-between items-center p-3 bg-gradient-to-r from-purple-500 to-indigo-600 text-white shadow-lg z-10 sticky top-0">
                <div className="flex items-center">
                    {/* 
                        모바일용 AI 프로필 이미지:
                        <img src="/images/ai-avatar.png" alt="AI" className="w-10 h-10 rounded-full mr-3 border-2 border-white" />
                    */}
                    <div>
                        <h1 className="text-base font-medium">Crosstalk AI</h1>
                        <span className="text-xs opacity-90">전문 상담사</span>
                    </div>
                </div>
                {activeScenarioIndex !== null && (
                    <span className="text-xs font-medium bg-white/20 px-2 py-1 rounded-full">
                        #{activeScenarioIndex + 1}
                    </span>
                )}
                <button
                    onClick={onExit}
                    className="flex items-center hover:bg-white/10 p-2 rounded-lg transition-colors"
                >
                    <LogOut size={18} />
                </button>
            </header>

            {/* 채팅 영역 - 모바일 최적화 */}
            <div className="flex-grow overflow-y-auto p-3 space-y-3 bg-gray-50">
                {messages.map((msg, index) => (
                    <div key={index} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                        <div
                            className={`px-4 py-3 max-w-[75%] break-words rounded-2xl text-sm ${
                                msg.sender === 'user' 
                                ? 'bg-gradient-to-r from-purple-500 to-indigo-600 text-white rounded-br-sm' 
                                : 'bg-white text-gray-800 shadow-sm rounded-bl-sm'
                            }`}
                        >
                            {msg.text}
                        </div>
                    </div>
                ))}
                {isAiTyping && (
                    <div className="flex justify-start">
                        <div className="px-4 py-3 bg-white rounded-2xl rounded-bl-sm shadow-sm">
                            <div className="flex space-x-1">
                                <div className="w-2 h-2 bg-purple-400 rounded-full animate-pulse [animation-delay:-0.3s]"></div>
                                <div className="w-2 h-2 bg-purple-400 rounded-full animate-pulse [animation-delay:-0.15s]"></div>
                                <div className="w-2 h-2 bg-purple-400 rounded-full animate-pulse"></div>
                            </div>
                        </div>
                    </div>
                )}
                {/* 스크롤을 위한 빈 div */}
                <div ref={chatEndRef}></div>
            </div>

            {/* 입력 영역 - 모바일 최적화 */}
            <footer className="flex-shrink-0 p-3 bg-white border-t border-gray-200 sticky bottom-0">
                <div className="flex items-end space-x-2">
                    <textarea
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyDown={handleKeyDown}
                        placeholder="메시지를 입력하세요..."
                        rows="1"
                        className="flex-grow p-3 border border-gray-300 rounded-2xl resize-none focus:outline-none focus:ring-2 focus:ring-purple-400 text-sm"
                        style={{ minHeight: '44px', maxHeight: '120px' }}
                    ></textarea>
                    <button
                        onClick={handleSend}
                        className="flex-shrink-0 p-3 bg-gradient-to-r from-purple-500 to-indigo-600 text-white rounded-full shadow-md hover:shadow-lg transition-shadow active:scale-95"
                        style={{ width: '44px', height: '44px' }}
                    >
                        <Send size={20} />
                    </button>
                </div>
            </footer>
        </div>
    );
}

export default MobileChatPage;
