import React, { useState, useEffect } from 'react';
import TopMenuBar from './TopMenuBar';

function MainButton({ text, active, onClick }) {
    const baseStyle = "p-8 rounded-2xl text-xl font-light transition-all duration-300 ease-in-out transform hover:scale-105 cursor-pointer focus:outline-none focus:ring-2 focus:ring-offset-2";
    const activeStyle = "bg-gradient-to-r from-purple-500 to-indigo-600 text-white shadow-lg hover:shadow-xl focus:ring-purple-500";
    const inactiveStyle = "bg-white text-gray-700 shadow-md hover:shadow-lg focus:ring-indigo-400";

    return (
        <button
            onClick={onClick}
            className={`${baseStyle} ${active ? activeStyle : inactiveStyle}`}
        >
            {text}
        </button>
    );
}

function MainPage({ onStartChat, notification, setPage }) {
    // 스크롤을 감지해 환영 페이지로 돌아가는 로직
    useEffect(() => {
        // 마우스 휠 스크롤 감지
        const handleScroll = (e) => {
            if (window.scrollY === 0 && e.deltaY < 0) {
                setPage('welcome');
            }
        };

        // 터치 스와이프 감지
        let touchStartY = 0;
        const handleTouchStart = (e) => {
            touchStartY = e.touches[0].clientY;
        };
        const handleTouchMove = (e) => {
            const touchEndY = e.touches[0].clientY;
            if (window.scrollY === 0 && touchEndY - touchStartY > 50) {
                setPage('welcome');
            }
        };

        window.addEventListener('wheel', handleScroll);
        window.addEventListener('touchstart', handleTouchStart, { passive: true });
        window.addEventListener('touchmove', handleTouchMove, { passive: true });

        return () => {
            window.removeEventListener('wheel', handleScroll);
            window.removeEventListener('touchstart', handleTouchStart);
            window.removeEventListener('touchmove', handleTouchMove);
        };
    }, [setPage]);

    return (
        <div className="min-h-screen w-full bg-white font-light">
            {/* 상단 메뉴바 */}
            <TopMenuBar onHomeClick={() => setPage('welcome')} />

            {/* 메인 콘텐츠 */}
            <main className="w-full pt-16">{/* pt-16으로 메뉴바 높이만큼 여백 */}
                <div className="min-h-[calc(100vh-4rem)] flex flex-col justify-center items-center text-center px-4 py-16">
                    {/* 
                        배경 이미지 넣기:
                        <div className="absolute inset-0 z-0">
                            <img src="/images/background.jpg" alt="" className="w-full h-full object-cover opacity-20" />
                        </div>
                    */}

                    <h2 className="text-5xl md:text-7xl font-thin text-gray-800" style={{ fontWeight: 200 }}>
                        다시 연결되는 순간
                    </h2>
                    <p className="mt-6 text-xl md:text-2xl text-gray-600 max-w-2xl">
                        이별 후의 감정, 혼자 감당하지 마세요.
                        Crosstalk AI가 당신의 마음을 듣고, 공감하고,
                        회복의 길을 함께 걷습니다.
                    </p>

                    {/* 4개 메인 버튼 (2x2 그리드) */}
                    <div className="mt-16 grid grid-cols-1 sm:grid-cols-2 gap-6 w-full max-w-lg">
                        <MainButton text="상담신청하기" active={false} />
                        <MainButton text="채팅시작하기" active={true} onClick={onStartChat} />
                        <MainButton text="문의하기" active={false} />
                        <MainButton text="다양한 소식" active={false} />
                    </div>
                </div>
            </main>

            {/* 알림 토스트 */}
            {notification && (
                <div className="fixed bottom-10 right-10 bg-green-500 text-white px-6 py-3 rounded-2xl shadow-lg animate-pulse">
                    {notification}
                </div>
            )}
        </div>
    );
}

export default MainPage;
